package basic_programs;

public class With_out_multipleOpperater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7,b=5;
		int sum=0;
		for(int i=1;i<=a;i++)
		{
			sum=sum+b;
			//System.out.println (i+sum);
		}
        System.out.println(a+"*"+b +"="+sum);
		//System.out.println(a*b);
		System.out.println(a+"*"+b +":"+sum);
	}

}
